import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class News extends Component {


        constructor(){
            super();
            this.state = {
                articles:[],
                loading:false,
                page: 1
        }
        console.log('hii i am constructor');
    }

     async componentDidMount(){

        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=${ this.props.newsKey}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parseData = await data.json();

        this.setState({ articles:parseData.articles,totalResult:parseData.totalResults});

    }

    handleNextButton = async ()=>{
     if(this.state.page + 1 >Math.ceil(this.state.totalResult/this.props.pageSize)){
            console.log('no data');
     }else{
             let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=6ebc2a61e4214ddebacf3db11107688e&page=${this.state.page+1}&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parseData = await data.json();

        this.setState({ articles:parseData.articles , page:this.state.page+1});
    }

    }

    handPreviousButton = async ()=>{
       if(this.state.page > 1){
         let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=6ebc2a61e4214ddebacf3db11107688e&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;

        let data = await fetch(url);
        let parseData = await data.json();

        this.setState({ articles:parseData.articles , page:this.state.page-1});
    }
    }



    render() {
        return (

            <div>

            <div className="container mt-5">
        <div className="row">
        {this.state.articles.map((element,index)=>{

           return <div className="col-4 my-5" key={index}>
            <NewsItems title={element.title} description={element.description} urlToImage={element.urlToImage} url={element.url}/>
        </div>

        })}


                </div>
                </div>


                <div className="container d-flex justify-content-between">
                <button disabled={this.state.page <= 1} type="button" onClick={ this.handPreviousButton} className="btn btn-success"> &larr; Previous</button>
                    <button disabled={this.state.page + 1 >Math.ceil(this.state.totalResult/this.props.pageSize)} type="button" onClick={ this.handleNextButton} className="btn btn-danger"> &rarr;Next</button>
                </div>

            </div>
        )
    }
}

export default News
