import React, { Component } from 'react'
import NewsItems from './NewsItems'
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from 'react-infinite-scroll-component';

export class News extends Component {

    static defaultProps = {
            'country' : "in",
            'pageSize' : 6,
            'newsKey'  :"6ebc2a61e4214ddebacf3db11107688e",
            'category' : 'general', 
    }
    static propTypes = {
             'country' : PropTypes.string,
            'pageSize' : PropTypes.number,
            'newsKey': PropTypes.string,
            'category' : PropTypes.string
    }
     capitalize(str) {
  const lower = str.toLowerCase();
  return str.charAt(0).toUpperCase() + lower.slice(1);
}

        constructor(props){
            super(props);
            this.state = {
                articles:[],
                loading:true,
                page: 1,
                totalResults:0
        }
        document.title = `${this.capitalize(this.props.category)} - News app`;

    }

    async updateNews(){
        this.props.setProgress(10);
         this.setState({ loading:true});
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.setCountry}&category=${this.props.category}&apiKey=${ this.props.newsKey}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
         this.props.setProgress(50);
        let data = await fetch(url);
        let parseData = await data.json();
        this.props.setProgress(70);
        this.setState({ articles:parseData.articles,totalResults:parseData.totalResults,loading:false});

          this.props.setProgress(100);
    }


    fetchMoreData = async () =>{
        console.log(this.state.page);
 this.setState({  page:this.state.page+1});
 console.log(this.state.page);
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.setCountry}&category=${this.props.category}&apiKey=${ this.props.newsKey}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parseData = await data.json();

        this.setState({ articles:this.state.articles.concat(parseData.articles),totalResults:parseData.totalResults,loading:false});
    }

     async componentDidMount(){

         this.updateNews();

    }



    render() {
        return (

            <div>
            <h1 className="text-center">News App Top {this.capitalize(this.props.category)} Headlines</h1>
             { this.state.loading && <Spinner/>}  

             <InfiniteScroll
    dataLength={this.state.articles.length}
    next={this.fetchMoreData}
    hasMore={this.state.articles.length != this.state.totalResults}
    loader={<Spinner/>}

  >
   <div className="container mt-5">
        <div className="row">

        {this.state.articles.map((element,index)=>{

           return <div className="col-4 my-5" key={index}>
            <NewsItems source={element.source.name} author={element.author} publishedAt={element.publishedAt} title={element.title} description={element.description} urlToImage={element.urlToImage} url={element.url}/>
        </div>

        })}

          </div>
                </div>
                     </InfiniteScroll>






            </div>
        )
    }
}

export default News
