import React, { Component } from 'react'
import NewsItems from './NewsItems'
import Spinner from './Spinner';
import PropTypes from 'prop-types';

export class News extends Component {

    static defaultProps = {
            'country' : "in",
            'pageSize' : 6,
            'newsKey'  :"6ebc2a61e4214ddebacf3db11107688e",
            'category' : 'general', 
    }
    static propTypes = {
             'country' : PropTypes.string,
            'pageSize' : PropTypes.number,
            'newsKey': PropTypes.string,
            'category' : PropTypes.string
    }

        constructor(props){
            super(props);
            this.state = {
                articles:[],
                loading:false,
                page: 1
        }
        document.title = this.props.category+" - News app";

    }

    async updateNews(){
         this.setState({ loading:true});
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.setCountry}&category=${this.props.category}&apiKey=${ this.props.newsKey}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parseData = await data.json();

        this.setState({ articles:parseData.articles,totalResult:parseData.totalResults,loading:false});
    }

     async componentDidMount(){
       // this.setState({ loading:true});
       //  let url = `https://newsapi.org/v2/top-headlines?country=${this.props.setCountry}&category=${this.props.category}&apiKey=${ this.props.newsKey}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
       //  let data = await fetch(url);
       //  let parseData = await data.json();

       //  this.setState({ articles:parseData.articles,totalResult:parseData.totalResults,loading:false});
         this.updateNews();

    }

    handleNextButton = async ()=>{
    //  if(!(this.state.page + 1 >Math.ceil(this.state.totalResult/this.props.pageSize))){
    //         console.log('no data');


    //      this.setState({ loading:true});
    //          let url = `https://newsapi.org/v2/top-headlines?country=${this.props.setCountry}&category=${this.props.category}&apiKey=${ this.props.newsKey}&page=${this.state.page+1}&pageSize=${this.props.pageSize}`;
    //     let data = await fetch(url);
    //     let parseData = await data.json();

    //     this.setState({ articles:parseData.articles , page:this.state.page+1,loading:false});
    // }
        this.setState({  page:this.state.page+1});
        this.updateNews();
    }

    handPreviousButton = async ()=>{
    //    if(this.state.page > 1){
    //      this.setState({ loading:true});
    //      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.setCountry}&category=${this.props.category}&apiKey=${ this.props.newsKey}&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;

    //     let data = await fetch(url);
    //     let parseData = await data.json();

    //     this.setState({ articles:parseData.articles , page:this.state.page-1,loading:false});
    // }
    this.setState({  page:this.state.page-1});
     this.updateNews();
    }



    render() {
        return (

            <div>
            <h1 className="text-center">News App</h1>

            <div className="container mt-5">
        <div className="row">
        { this.state.loading && <Spinner/>}
        { !this.state.loading && this.state.articles.map((element,index)=>{

           return <div className="col-4 my-5" key={index}>
            <NewsItems source={element.source.name} author={element.author} publishedAt={element.publishedAt} title={element.title} description={element.description} urlToImage={element.urlToImage} url={element.url}/>
        </div>

        })}


                </div>
                </div>

                <div className="container d-flex justify-content-between">
                <button disabled={this.state.page <= 1} type="button" onClick={ this.handPreviousButton} className="btn btn-success"> &larr; Previous</button>
                    <button disabled={this.state.page + 1 >Math.ceil(this.state.totalResult/this.props.pageSize)} type="button" onClick={ this.handleNextButton} className="btn btn-danger"> &rarr;Next</button>
                </div>

            </div>
        )
    }
}

export default News
